-- ----------------------------
-- Table structure for `news`
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `userId` int(10) NOT NULL,
  `newsId` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `newsType` varchar(255) NOT NULL,
  `author` varchar(255) NOT NULL,
  `caption` varchar(255) NOT NULL,
  `content` text NOT NULL,
  `newsTime` datetime NOT NULL COMMENT '新闻日期',
  `publishTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '新闻发布日期',
  `newsEnable` enum('use','stop') NOT NULL DEFAULT 'stop' COMMENT '0表示没通过审核，1表示通过审核',
  PRIMARY KEY (`newsId`),
  KEY `newsTime1` (`newsTime`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8;

