-- ----------------------------
-- Table structure for `users`
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `userId` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `userType` enum('manager','newsAuthor','user') NOT NULL DEFAULT 'user',
  `username` varchar(255) NOT NULL,
  `password` varchar(255) NOT NULL,
  `userImage` varchar(255) NOT NULL DEFAULT '',
  `registerDate` varchar(255) NOT NULL,
  `userEnable` enum('stop','use') NOT NULL DEFAULT 'stop',
  `userGender` varchar(255) DEFAULT NULL,
  `userHobby` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`userId`,`username`),
  UNIQUE KEY `name` (`username`),
  KEY `type` (`userType`)
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8;

